mapper.select:
select d.id,t.device_code,t.suitable,t.shed_name,t.bed_name,t.birth_date,t.entry_date,t.supplier_name,d.dead_date,d.dict_id,d.dict_name from t_livestock t,t_livestock_dead d
where t.livestock_id=d.livestock_id and t.delete_flag = 1

    and t.dept_id in (${vo.deptIds})


    and t.suitable = #{vo.suitable}


    and t.shed_id = #{vo.shedId}


    and t.bed_id = #{vo.bedId}


    and d.dict_id = #{vo.dictId}


    and d.dead_date >= #{vo.startTime}


    and d.dead_date <= #{vo.endTime}

ORDER BY d.dead_date desc

select d.id,t.device_code,t.suitable,t.shed_name,t.bed_name,t.birth_date,t.entry_date,t.supplier_name,d.dead_date,d.dict_id,d.dict_name,t.credit_code from t_livestock_log t,t_livestock_dead d
where t.livestock_id=d.livestock_id and t.delete_flag = 0

    and d.id = #{vo.id}
