mapper.select:
select t.device_code,t.suitable,t.shed_name,t.bed_name,t.birth_date,t.entry_date,t.supplier_name,d.* from t_livestock_escape d ,
t_livestock t where  t.livestock_id=d.livestock_id and t.delete_flag = 1

    and t.dept_id in (${vo.deptIds})


    and t.suitable = #{vo.suitable}


    and t.shed_id = #{vo.shedId}


    and t.bed_id = #{vo.bedId}


    and d.dict_id = #{vo.dictId}


    and d.escape_date >= #{vo.startTime}


    and d.escape_date <= #{vo.endTime}

ORDER BY t.create_time desc

select t.device_code,t.suitable,t.shed_name,t.bed_name,t.birth_date,t.entry_date,t.supplier_name,d.* from t_livestock_log t,t_livestock_escape d
where t.livestock_id=d.livestock_id and t.delete_flag = 0

    and d.id = #{vo.id}
