mapper.update:
update t_device_electric


        alarm_temperature = #{vo.alarmTemperature},


        alarm_electric = #{vo.alarmElectric},


        alarm_electric_leakage = #{vo.alarmElectricLeakage}


where imei = #{vo.imei}

mapper.select:
select * from t_device_electric t where t.delete_flag = 0

    and t.id = #{vo.id}


    and t.imei = #{vo.imei}

order by t.create_time desc limit 1
